# game methods
def print_pipe(pos_x_p1, pos_y_p1) :
    djb.display.sprite(3, pos_x_p1, pos_y_p1, ALPHA_COLOR)
    djb.display.sprite(4, pos_x_p1, pos_y_p1 + PIPE_HEIGHT + HOLE_SIZE, ALPHA_COLOR)

def generate_pipe():
    pos_y_p = randint(-PIPE_HEIGHT,-PIPE_HEIGHT + djb.display.height//2)
    pos_x_p = djb.display.width
    return pos_x_p, pos_y_p

def collision(x1,y1,w1,h1,x2,y2,w2,h2):
    return x1 + w1 >= x2 and x1 <= x2 + w2 and y1 + h1 >= y2 and y1 <= y2 + h2

# game loop
while True:
    
    #game settings

    counter = 0
    flappy_sprite_condition = False
    gameover = False
    score = 0
    
    #sprite settings
    flappy_sprite = 0
    pos_x = 50
    pos_y = 50
    velocity_y = 0.0
    delta_bonus = 74

    #floor settings
    pos_x_floor = 0

    #pipe 1 initialisation
    pos_x_p1, pos_y_p1 = generate_pipe()

    #pipe 2 initialisation
    pos_x_p2, pos_y_p2 = generate_pipe()
    pos_x_p2 += 90
    
    pos_x_b = djb.display.width + delta_bonus
    pos_y_b = 0
    show_bonus = False

    #part_game loop
    while True:
               
        # update the screen :
           
        #the background
        djb.display.fill(BACKGROUND_COLOR)
       
        #the main sprite (flappy)
        djb.display.sprite(flappy_sprite, int(pos_x), int(pos_y), ALPHA_COLOR)
        
        if flappy_sprite_condition == True :
            counter = counter + 1
            
            if flappy_sprite >= 2 :
                    flappy_sprite = 0
                    flappy_sprite_condition = False
            
            if counter == 3  :
                flappy_sprite = flappy_sprite + 1
                counter = 0
                    
        #pipe 1
        print_pipe(pos_x_p1, pos_y_p1)

        #pipe 2
        print_pipe(pos_x_p2, pos_y_p2)
        
        if show_bonus:
            djb.display.sprite(6, int(pos_x_b), int(pos_y_b), ALPHA_COLOR)
            
        #floor
        for i in range(0,3):        
            djb.display.sprite(5, pos_x_floor + i*98,POS_Y_FLOOR)

        #the score
        djb.display.top_right_corner_text('Score {}'.format(score), djb.display.WHITE_H)
        
        djb.display.show_and_wait()
       
        velocity_y = velocity_y + 0.20 # 0.20
        pos_y = pos_y + velocity_y
             
        djb.scan_jst_btn()
            
        if djb.just_pressed(djb.btn_A) or djb.just_pressed(djb.btn_B):
            flappy_sprite_condition = True
            velocity_y = -2.4 # -4.4
            
        #update the pipes position
        pos_x_p1 = pos_x_p1 - SPEED
        pos_x_p2 = pos_x_p2 - SPEED

        pos_x_b = pos_x_b - SPEED
        
        #generate pipe 1
        if pos_x_p1 < -26 :                    
            pos_x_p1, pos_y_p1 = generate_pipe()
            score += 10
        
        #generate pipe 2
        if pos_x_p2 < -26 :
            pos_x_p2, pos_y_p2 = generate_pipe()
            score += 10

        #bonus time ?
        if pos_x_b < -22 or show_bonus == False:
            random_bonus = randint(0,10)
            if random_bonus == 0:
                show_bonus = True
                pos_x_b = pos_x_p1 + delta_bonus + djb.display.width
                pos_y_b = randint(0,djb.display.height-22-14)

        #update the floors positions
        pos_x_floor = pos_x_floor - SPEED
        
        if pos_x_floor <= -98:
            pos_x_floor = pos_x_floor + 98    
     
        #Hit Box verification...
        if collision(pos_x, pos_y, 19, 14, pos_x_p1 + 2, pos_y_p1, 26, 100-2): # top pipe 1
            print('touch P1 Top')
            gameover = True
        
        if collision(pos_x, pos_y, 19, 14, pos_x_p1 + 2, pos_y_p1 + 100 + HOLE_SIZE + 2, 26, 100): # bottom pipe 1
            print('touch P1 Bot')
            gameover = True
            
        if collision(pos_x, pos_y, 19, 14, pos_x_p2 + 2, pos_y_p2, 26, 100-2): # top pipe 2
            print('touch P2 Top')
            gameover = True           
            
        if collision(pos_x, pos_y, 19, 14, pos_x_p2 + 2, pos_y_p2 + 100 + HOLE_SIZE + 2, 26, 100): # bottom pipe 2
            print('touch P2 Bot')
            gameover = True

        if collision(pos_x, pos_y, 19, 14, pos_x_b + 2, pos_y_b, 22, 22) and show_bonus == True: # bonus touch ?
            score = score + 50
            show_bonus = False

        if pos_y + 10 > POS_Y_FLOOR :
            print('touch Floor')
            gameover = True
            
        if gameover:
            break
        
    #Game over
    djb.display.center_text("GAME OVER",djb.display.WHITE_H, 2)
    djb.display.show()
    
    time.sleep(1)

    while not djb.scan_jst_btn():
        time.sleep(0.1)                  
